using AngryMonkey.Cloud.Components.Icons;
using Microsoft.AspNetCore.Components;
using Microsoft.JSInterop;
using QRCoder;
using System.Diagnostics.Metrics;
using ZXing;
using ZXing.QrCode;

namespace Qrcode.Pages
{
    public partial class Index
    {
        public int Type { get; set; } = 0;
        public string Svg { get; set; }
        public string Url { get; set; }
        public string email { get; set; }
        public string Name { get; set; } = "";
        public string Firtname
        {
            get
            {
                return Name.Split(" ")[0];
            }
        }
        public string Lastname
        {
            get
            {
                if (Name.Split(" ").Count() > 1)
                    return Name.Split(" ")[1].ToString();

                return "";
            }
        }
        public string Work { get; set; }
        public string Phone { get; set; }
        MarkupString Ms => (MarkupString)Svg;
        public string VsCard { get; set; }

        public void Generate()
        {

            if (Type == 0)
            {
                VsCard = $"BEGIN:VCARD\n" +
                $"VERSION:3.0\n" +
                $"N:{Lastname};{Firtname}\n" +
                $"FN:{Name}\n" +
                $"ORG:{Work}\n" +
                $"TITLE:\n" +
                $"TEL;TYPE=WORK:{Phone}\n" +
                $"EMAIL;INTERNET:{email}\n" +
                $"END:VCARD";

                GenerateQrCode(VsCard);

            }
            else if (Type == 1)
            {
                GenerateQrCode(Url);
            }

        }

        public async Task Download()=> await JS.InvokeAsync<object>("download", "QrCode.svg", Svg, "text/plain");

        protected void GenerateQrCode(string Value) => Svg = RenderQrCode(Value);
        

        private string RenderQrCode(string Value)
        {
            if (string.IsNullOrEmpty(Value))
                return null;

            string level = "L";

            QRCodeGenerator.ECCLevel eccLevel = (QRCodeGenerator.ECCLevel)(level == "L" ? 0 : level == "M" ? 1 : level == "Q" ? 2 : 3);

            using QRCodeGenerator qrGenerator = new();
            using QRCodeData qrCodeData = qrGenerator.CreateQrCode(Value, eccLevel);
            using SvgQRCode qrCode = new(qrCodeData);

            qrCode.Instructions = QRCodeInstructions();

            string svg = qrCode.GetGraphic(20, "#000000", "#ffffff", sizingMode: SvgQRCode.SizingMode.ViewBoxAttribute);

            return svg;
        }

